using HalloWelt2V2.Classes;namespace HalloWelt2V2{	//This is a revised version of my Hello World 2 console application to show my progress in C# and programming.	public class Program	{		static void Main(string[] args)		{			Breeder breeder = new Breeder();			IOManager ioManager = new IOManager();			Console.WriteLine("Guten Tag, ich bin das Haustierprogramm.\nMit wem kommuniziere ich?");			String userName = Console.ReadLine();			Console.WriteLine($"\n{userName}? Das ist aber ein schöner Name.\nBist du denn männlich (\"M\") oder weiblich (\"W\") oder divers (\"D\")?");			Genders userGender = ioManager.GetUserGender();			Console.WriteLine("Wie alt sind Sie?");			Int32 userAge = ioManager.CheckIfAgeIsValid(ioManager.ValidateInputNumber());			Console.WriteLine("Das Haustierprogramm stellt dir verschiedene Hunde und Katzen vor.\nEntscheide dich für eins dieser Tiere:\n- Hund (\"H\")\n- Katze (\"K\")\n");			User user = new User(userName, userAge, userGender);			IEnumerable<Animal> chosenAnimalsList = new List<Animal>();						if (ioManager.ChooseCatOrDog())			{				breeder.PresentDogs();				chosenAnimalsList = breeder.dogs;			}			else			{				breeder.PresentCats();				chosenAnimalsList = breeder.cats;			}			Console.WriteLine("Bitte geben Sie die Zahl vor dem jeweiligen Tier ein, um es auszuwählen.");			Animal chosenAnimal = breeder.ChooseAnimal(ioManager.ValidateInputNumber(), chosenAnimalsList);			Console.WriteLine($"\nSie haben sich für {chosenAnimal.Name} entschieden.");						breeder.LetAnimalDoSomething(chosenAnimal);			Console.WriteLine("\nEnde.");		}	}}